import { Curso } from './../model/curso';
import { Component, OnInit } from '@angular/core';
import { CursosService } from '../services/cursos.service';
import { catchError, Observable, of } from 'rxjs';

@Component({
  selector: 'app-cursos',
  templateUrl: './cursos.component.html',
  styleUrls: ['./cursos.component.css']
})
export class CursosComponent implements OnInit {

  cursos$: Observable<Curso[]>;
  displayedColumns = ['name', 'categoria'];



  constructor(private cursosService: CursosService) {

    this.cursos$ = this.cursosService.list()
    .pipe(
      catchError(error => {
        console.log(error);
        return of([])
      })
    );

    // this.courses = []; (pode ser assim tmb para instanciar uma classe)
   }

  ngOnInit(): void {
  }

}
<mat-card>
  <mat-toolbar>
    <span> Cursos </span>
  </mat-toolbar>
  <div *ngIf="cursos$ | async as cursos; else loading">
    <table mat-table [dataSource]="cursos" class="mat-elevation-z8">
      <!-- Coluna de Nome -->
      <ng-container matColumnDef="name">
        <th mat-header-cell *matHeaderCellDef>Nome do Curso</th>
        <td mat-cell *matCellDef="let curso">{{ curso.name }}</td>
      </ng-container>

      <ng-container matColumnDef="categoria">
        <th mat-header-cell *matHeaderCellDef>Categoria</th>
        <td mat-cell *matCellDef="let curso">{{ curso.categoria }}</td>
      </ng-container>

      <ng-container matHeaderCellDef="actions">
        <mat-header-cell>
          <button mat-mini-fab color="accent" aria-label="Example icon button with a plus one icon">
          <mat-icon>add</mat-icon>
        </button></mat-header-cell>
      </ng-container>

      <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
      <tr mat-row *matRowDef="let row; columns: displayedColumns"></tr>
    </table>
  </div>
  <ng-template #loading>
    <div class="loading-spinner">
      <mat-spinner></mat-spinner>
    </div>
  </ng-template>
</mat-card>
